    void drawSurface(igl::opengl::glfw::Viewer &viewer, double resolution = 0.01, Eigen::RowVector3d color = Eigen::RowVector3d(1, 0, 0));


    bool isRational = false;
    int n; // P_0,P_1,...,P_n; _n is the final index
    int k; // order of BSpline
